mapper.insert:
        INSERT /* com.theshoes.jsp.board.model.dao.QuestionDAO#insertQuestionFile() */
          INTO CS_BOARD_FILE A
       	  (
			   A.CS_FILE_NO
			 , A.CS_BOARD_NO
   			 , A.CS_ORIGINAL_NAME
			 , A.CS_SAVED_NAME
			 , A.CS_SAVE_PATH
			 , A.CS_FILE_TYPE
			 , A.CS_THUMBNAIL_PATH
			 , A.CS_ATTACHMENT_STATUS
			)
	        VALUES
			(
			  SEQ_CS_BOARD_FILE_CODE.NEXTVAL
			, SEQ_CS_BOARD_CODE.CURRVAL
			, #{ originalName }
			, #{ savedName }
			, #{ savePath }
			, #{ fileType }
			, #{ thumbnailPath }
			, 'Y'
			)

INSERT /* com.theshoes.jsp.board.model.dao.QuestionDAO#insertQuestion() */
  INTO CS_BOARD A
(
  A.CS_NO
, A.CS_ID
, A.CS_TITLE
, A.CS_CONTENT
, A.CS_REGDATE
)
VALUES 
(
  SEQ_CS_BOARD_CODE.NEXTVAL
, #{ csId }
, #{ csTitle }
, #{ csContent }
, #{ csRegDate }
)

	SELECT
	       SEQ_CS_BOARD_CODE.CURRVAL
	  FROM DUAL

INSERT /* com.theshoes.jsp.cs.model.dao.QuestionDAO#registNotice() */
  INTO CS_REQUEST A
(
  A.CS_REQUEST_NO
, A.MANAGER_ID
, A.REQUEST_CONTENT
, A.REQUEST_DATE
)
VALUES
(
  #{ csRequestNo }
, #{ managerId }
, #{ requestContent }
, #{ requestRegDate }
)
