mapper.insert:
   INSERT
		  INTO SHOES_INFO A
		(
		  A.SHOES_NO
		, A.INFO_CATEGORY_NO
		, A.SHOES_MODEL
		, A.SHOES_PRICE
		, A.SALES_YN
		, A.SALES_AMOUNT
		, A.REAMANING_AMOUNT
		, A.START_DATE
		, A.END_DATE
		, A.WINNER_DATE
		, A.EVENT_END_YN
		)
		VALUES
		(
		  SEQ_SHOES_CODE.NEXTVAL
		, #{ infoCategoryNo }
		, #{ shoesModel }
		, #{ shoesPrice }
		, #{ salesYn }
		, #{ salesAmount }
		, #{ salesAmount }
		, #{ startDate }
		, #{ endDate }
		, #{ winnerDate }
		, 'N'
		)
