mapper.insert:
   INSERT
		  INTO SHOES_INFO A
		(
		  A.SHOES_NO
		, A.INFO_CATEGORY_NO
		, A.SHOES_MODEL
		, A.SHOES_PRICE
		, A.SALES_YN
		, A.SALES_AMOUNT
		, A.REAMANING_AMOUNT
		, A.START_DATE
		, A.END_DATE
		, A.WINNER_DATE
		, A.EVENT_END_YN
		)
		VALUES
		(
		  SEQ_SHOES_CODE.NEXTVAL
		, #{ infoCategoryNo }
		, #{ shoesModel }
		, #{ shoesPrice }
		, #{ salesYn }
		, #{ salesAmount }
		, #{ salesAmount }
		, #{ startDate }
		, #{ endDate }
		, #{ winnerDate }
		, 'N'
		) 
		
			SELECT
				   SEQ_SHOES_CODE.CURRVAL
			  FROM DUAL

INSERT 
	  INTO SHOES_THUMB A
	(
	  A.SHOES_THUMB_NO
	, A.ST_NO
	, A.ORIGINAL_NAME
	, A.SAVED_NAME
	, A.SAVE_PATH
	, A.FILE_TYPE
	, A.THUMBNAIL_PATH
	, A.ATTACHMENT_STATUS
	)
	VALUES
	(
	  SEQ_SHOES_THUMB_CODE.NEXTVAL
	, SEQ_SHOES_CODE.CURRVAL
	, #{ originalName }
	, #{ savedName }
	, #{ savePath }
	, #{ fileType }
	, #{ thumbnailPath }
	, 'Y'
	)
