mapper.insert:
   INSERT
		  INTO SHOES_INFO A
		(
		  A.SHOES_NO
		, A.INFO_CATEGORY_NO
		, A.SHOES_MODEL
		, A.SHOES_PRICE
		, A.SALES_YN
		, A.SALES_AMOUNT
		, A.REAMANING_AMOUNT
		, A.START_DATE
		, A.END_DATE
		, A.WINNER_DATE
		, A.EVENT_END_YN
		)
		VALUES
		(
		  SEQ_SHOES_CODE.NEXTVAL
		, #{ infoCategoryNo }
		, #{ shoesModel }
		, #{ shoesPrice }
		, #{ salesYn }
		, #{ salesAmount }
		, #{ salesAmount }
		, #{ startDate }
		, #{ endDate }
		, #{ winnerDate }
		, #{ eventEndYn }
		) 
		
			SELECT
				   SEQ_SHOES_CODE.CURRVAL
			  FROM DUAL

mapper.update:
UPDATE
       SHOES_INFO A
   SET A.INFO_CATEGORY_NO = #{ infoCategoryNo }
     , A.SHOES_MODEL = #{ shoesModel }
     , A.SHOES_PRICE = #{ shoesPrice }
     , A.SALES_YN = #{ salesYn }
     , A.SALES_AMOUNT = #{ salesAmount }
     , A.START_DATE = #{ startDate }
     , A.END_DATE = #{ endDate }
     , A.WINNER_DATE = #{ winnerDate }
     , A.EVENT_END_YN = #{ eventEndYn }
 WHERE A.SHOES_NO = #{ shoesNo }

mapper.insert:
INSERT 
	  INTO SHOES_THUMB A
	(
	  A.SHOES_THUMB_NO
	, A.ST_NO
	, A.ORIGINAL_NAME
	, A.SAVED_NAME
	, A.SAVE_PATH
	, A.FILE_TYPE
	, A.THUMBNAIL_PATH
	, A.ATTACHMENT_STATUS
	)
	VALUES
	(
	  SEQ_SHOES_THUMB_CODE.NEXTVAL
	, SEQ_SHOES_CODE.CURRVAL
	, #{ originalName }
	, #{ savedName }
	, #{ savePath }
	, #{ fileType }
	, #{ thumbnailPath }
	, 'Y'
	)

mapper.update:
UPDATE
       SHOES_THUMB A
   SET A.ORIGINAL_NAME = #{ originalName }
     , A.SAVED_NAME = #{ savedName }
     , A.SAVE_PATH = #{ savePath }
     , A.FILE_TYPE = #{ fileType }
     , A.THUMBNAIL_PATH = #{ thumbnailPath }
 WHERE A.SHOES_NO = #{ shoesNo }
